mapper.select:
select
df.form_num,df.form_title,ds.doc_Sort_name,df.form_cnt,df.write_time,
e.emp_name
from doc_form df, doc_sort ds, employee e
where df.emp_num = e.emp_num and df.doc_sort_num=ds.doc_sort_num
and act=true


	and df.doc_sort_num = #{param1}



	and df.form_title = #{param2}

 order by form_num DESC limit 10
offset #{param3}
;

	select count(form_num) from doc_form where act=true


		and doc_sort_num = #{param1}



		and form_title = #{param2}

;
