mapper.select:
select e.emp_num, e.emp_name,
t.team_name, p.pos_name, rank_num from employee e , team t ,
`position` p where e.team_num =t.team_num and p.pos_num =e.pos_num;

SELECT s.section_num ,
s2.store_name from section s , store s2 where s.section_num
=s2.section_num and emp_num = #{param1};

		SELECT store_name, s.section_num, s.minor_category_num, s.store_num from store s, `section` s3  WHERE s3.section_num = s.section_num and s3.emp_num = #{param1} and not store_name in (SELECT s.store_name from store s, `section` s2,doc_sales ds  WHERE s.section_num = s2.section_num and ds.store_num = s.store_num 
and s2.emp_num = #{param1} and ds.doc_date = #{param2}
);

SELECT d.doc_num, ds.doc_sort_name, d.doc_sub, d.doc_reg from doc d, doc_sort ds WHERE d.doc_sort_num = ds.doc_sort_num and d.emp_num = #{param1} and 

d.doc_state_num=1;


d.doc_state_num !=1;
