mapper.select:
SELECT t.team_num ,t.team_name FROM departure d ,team t
	WHERE t.dep_num = #{param1} 
	AND t.dep_num = d.dep_num

SELECT * FROM fac_book fb 
	WHERE book_start BETWEEN #{book_start} AND #{book_end}
	AND emp_num = #{emp_num}

mapper.update:
UPDATE fac_book 
	SET 
	fac_num = #{fac_num},
	book_start = #{book_start}, 
	book_end = #{book_end} 
	WHERE book_num = #{book_num}

UPDATE fac_book_league 
	SET 
	emp_num = #{param1}
	WHERE 	book_num = #{param2}
